var Node           = require('famous/core/Node');
var DOMElement     = require('famous/dom-renderables/DOMElement');

var FooterBtn      = require('./footer/FooterBtn');

var footerBtns = ['首页','分类','店铺','我的'];
var footerBtnNode = [];

function Footer(){
	Node.call(this);

	this
		.setSizeMode('relative','absolute')
		.setAbsoluteSize(null,50)
		.setMountPoint(1,1)
		.setAlign(1,1)
		.el = new DOMElement(this,{
			classes: ['footer']
		});
	
	addBtn(this);	
}

Footer.prototype = Object.create(Node.prototype);
Footer.prototype.constructor = Footer;


function addBtn(node){
	for(var i = 0; i < footerBtns.length; i++){
		footerBtnNode[i] = new FooterBtn({
			w: 1/footerBtns.length,
			l: i/footerBtns.length,
			c: footerBtns[i]
		});
		i == 0 && footerBtnNode[i].el.addClass('active');
		node.addChild(footerBtnNode[i]);
	}
}


module.exports = Footer;