var Node           = require('famous/core/Node');
var DOMElement     = require('famous/dom-renderables/DOMElement');

var EWM            = require('./header/EWM');
var Search         = require('./header/Search');


function Header(){
	Node.call(this);

	this
		.setSizeMode('relative','absolute')
		.setAbsoluteSize(null,45)
		.el = new DOMElement(this,{
			classes: ['header']
		});

		this.addChild(new EWM());
		this.addChild(new Search());
}

Header.prototype = Object.create(Node.prototype);
Header.prototype.constructor = Header;

module.exports = Header;