var Node         = require('famous/core/Node');
var DOMElement   = require('famous/dom-renderables/DOMElement');
var Banner       = require('./center/Banner');


function HomeCenter(){
	Node.call(this);

	this
		.setDifferentialSize(0,-95)
		.setPosition(0,45)
		.el = new DOMElement(this,{
			classes: ['center']
		});

		this.addChild(new Banner());
}

HomeCenter.prototype = Object.create(Node.prototype);
HomeCenter.prototype.constructor = HomeCenter;

module.exports = HomeCenter;