var Node = require('famous/core/Node');
var DOMElement   = require('famous/dom-renderables/DOMElement');

function Banner(){
	Node.call(this);

	this
		.setSizeMode('relative','absolute')
		.setAbsoluteSize(null,1000)
		.el = new DOMElement(this,{
			classes: ['banner']
		});

		new DOMElement(this.addChild()
				.setProportionalSize(1,1),{
			tagName: 'img'
		}).setAttribute('src','http://g5.qqnimg.com/erp_20151106112944223649_881d97.png');
}

Banner.prototype = Object.create(Node.prototype);
Banner.prototype.constructor = Banner;


function addBannerList(){

}

module.exports = Banner;