var Node = require('famous/core/Node');
var DOMElement   = require('famous/dom-renderables/DOMElement');

/*
	opt.w  宽度
	opt.l  左边定位
	opt.c  内容

*/
function FooterBtn(opt){
	Node.call(this);

	this
		.setProportionalSize(opt.w,1)
		.setAlign(opt.l,0)
		.el = new DOMElement(this,{
			classes: ['footer_btn'],
			content: opt.c
		});
}

FooterBtn.prototype = Object.create(Node.prototype);
FooterBtn.prototype.constructor = FooterBtn;

module.exports = FooterBtn;