var Node = require('famous/core/Node');
var DOMElement   = require('famous/dom-renderables/DOMElement');

function EWM(){
	Node.call(this);
	this
		.setSizeMode('absolute','absolute')
		.setAbsoluteSize(45,45)
		.el = new DOMElement(this,{
			classes: ['evm']
		})
}

EWM.prototype = Object.create(Node.prototype);
EWM.prototype.constructor = EWM;


module.exports = EWM;