var Node           = require('famous/core/Node');
var DOMElement     = require('famous/dom-renderables/DOMElement');

function Search(){
	Node.call(this);
	this
		.setDifferentialSize(-45,0)
		.setPosition(45,0)
		.el = new DOMElement(this,{
			classes: ['search']
		});


	addInputBox(this);	
}

Search.prototype = Object.create(Node.prototype);
Search.prototype.constructor = Search;


function addInputBox(node) {
	var searchInput = node.addChild();
	searchInput
		.setDifferentialSize(-10,-10)
		.setPosition(5,5)
		.el = new DOMElement(searchInput,{
			tagName: 'input'
		})
		.setAttribute('placeholder','搜索商品/店铺');

}



module.exports = Search;